///////////////////////////////////////////////////////////////////
// serializer                                                    //
///////////////////////////////////////////////////////////////////
inline void serialize(vector4 &o, ISerializer &s) {
  if(s.node_begin("vector4", rose::hash("vector4"), &o)) {
    s.key("w");
    serialize(o.w, s);
    s.key("x");
    serialize(o.x, s);
    s.key("y");
    serialize(o.y, s);
    s.key("z");
    serialize(o.z, s);
    s.node_end();
  }
  s.end();
}

///////////////////////////////////////////////////////////////////
// deserializer                                                  //
///////////////////////////////////////////////////////////////////
inline void deserialize(vector4 &o, IDeserializer &s) {
  construct_defaults(o);

  while (s.next_key()) {
    switch (s.hash_key()) {
      case rose::hash("w"):
        deserialize(o.w, s);
        break;
      case rose::hash("x"):
        deserialize(o.x, s);
        break;
      case rose::hash("y"):
        deserialize(o.y, s);
        break;
      case rose::hash("z"):
        deserialize(o.z, s);
        break;
      default: s.skip_key(); break;
    }
  }
}

///////////////////////////////////////////////////////////////////
// randomize                                                     //
///////////////////////////////////////////////////////////////////
inline void randomize(vector4 &o, rose::hash_value & h) {
  randomize(o.w, h);
  randomize(o.x, h);
  randomize(o.y, h);
  randomize(o.z, h);
}

///////////////////////////////////////////////////////////////////
// hashing                                                       //
///////////////////////////////////////////////////////////////////
  inline hash_value hash(const vector4 &o) {
    hash_value h = hash(o.w);
    h = xor64(h);
    h ^= hash(o.x);
    h = xor64(h);
    h ^= hash(o.y);
    h = xor64(h);
    h ^= hash(o.z);
    return h;
  }
